import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import VisibilityIcon from '@mui/icons-material/Visibility';
import EditIcon from '@mui/icons-material/Edit';
import Insert_attandance from '../form/Insert_attandance';



const Attandance = () => {
  const [search, setSearch] = useState("");
  const [c_data, setc_data] = useState([]);
  const [filterdata, setfilter] = useState([]);
  const [show,setshow] = useState(false);


  const handleClickOpen = async (id) => {
    setOpen(true);
    const data = await axios.get(`http://192.168.1.39:8070/fees/viewById/${id}`).then((res) => {
      console.log(res.data.data)
      setData(res.data.data)
    }).catch((err) => {
      console.log(err);
    })
  };
  const handleClose = () => {
    setOpen(false);
  
  };


  // -----------------------------datatable
  const getdata = async () => {
    try {
      const res = await axios.get("http://192.168.1.39:8070/attendance/view");
    
      setc_data(res.data.data);
      setfilter(res.data.data);
    
      
    }
    catch (error) {
      console.log(error);
    }
  };
const columns =[
  {
    name : "SID",
    selector : (row) => row.SID,

  },
  {
    name : "Rollno",
    selector : (row) => row.Rollno,

  },
  {
    name : "Class",
    selector : (row) => row.Class,

  },
  {
    name : "date",
    selector : (row) => (row.Attendance[0].date),

  },
  {
    name : "presant",
    selector : (row) => (row.Attendance[0].presant),

  },

]
useEffect(() => {
  getdata();
}, []);

useEffect(() => {
  const result = c_data.filter(val => {
    return val.Name.toLowerCase().match(search.toLowerCase());

  });

  setfilter(result);
}, [search]);
// --------------------------------------------
  return (
    <>
     <h1 className=' text-center'>Attandance Data</h1>
        
        {
          show ?  <Insert_attandance /> : ""
        }
         <button onClick={()=>setshow(!show)} className="insert"> { show ? "X":"fees"}</button>
       {
          show ? "": <DataTable
          title="Attandance"
          columns={columns}
          data={filterdata}
          pagination
          fixedHeader
          fixedHeaderScrollHeight='550px'
          highlightOnHover
          subHeader
          subHeaderAlign="left"
          subHeaderComponent={
            <input
              type="text"
              placeholder='search'
              className='w-25 form-control'
              value={search}
              onChange={(event) => setSearch(event.target.value)} />
          }
        />
       }

       
       <Modal
        keepMounted
        open={open}
        onClose={handleClose}
        aria-labelledby="keep-mounted-modal-title"
        aria-describedby="keep-mounted-modal-description">
        <Box sx={style}>
        <div className="d-flex">
             <PersonIcon style={{"width":"100%", "height":"100px"}} />
        </div>
          <div className='d-flex view_staff'>
             <h6 >SID:</h6><p>{data.SID}</p>
          </div>
          <div className='d-flex view_staff'>
            <h6>Fees:</h6><p>{data.Fees}</p>
          </div>
          <div className='d-flex view_staff'>
          <h6>Date:</h6><p>{data.Date}</p>
          </div>
          <div className='d-flex view_staff'>
            <h6>Installment:</h6><p>{data.Installment}</p>
          </div>
          
          
        </Box>
      </Modal>    
    </>
  )
}

export default Attandance
