import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'


const Table = () => {
  const [search,setSearch] = useState();
  const [c_data,setdata] = useState([]);
  const [filter,setfiter] = useState([]);
  const getdata = async ()=>{
      try{
        const res= await axios.get("https://restcountries.com/v2/all");
        setdata(res.data);
        setfiter(res.data);
        // console.log(res.data)
      }
      catch(error)
      {
        console.log(error);
      }
  };

const columns =[
  {
    name : "c_name",
    selector : (row) => row.name
    
  },
  {
    name : "c_name native",
    selector : (row) => row.nativeName
  },
  {
    name : "c_name capital",
    selector : (row) => row.capital
  },
  {
    name : "c_name alpha",
    selector : (row) => row.alpha2Code
  },
  {
     name: "Action",
     cell: (row) => <button className='btn btn-primary' 
     onClick={()=> alert("hello")}>Edit</button>
  }

]



useEffect( ()=>{
  getdata();
},[]);

useEffect( () =>{
   const result = c_data.filter(val=>{
    return val.name.toLowerCase().ma
   })
},[search]);
  return (
    <div>
    <h1 className=' text-center'>Data Table</h1>
    <DataTable
    title="list"  
    columns={columns}
     data={c_data} 
     pagination
      fixedHeader
      fixedHeaderScrollHeight='550px'
      selectableRows
      selectableRowsHighlight
      highlightOnHover
      actions={
        <button className='btn btn-sm btn-info'>export</button>
      }
      subHeader
      subHeaderComponent={
        <input type="text" placeholder='search' className='w-25 form-control' />
      }
      subHeaderAlign="right"
      value={search}
      onChange={ (e)=> setSearch(e.target.value)}
     />
    
    
    </div>
  )
}

export default Table
