import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import DeleteIcon from '@mui/icons-material/Delete';
import VisibilityIcon from '@mui/icons-material/Visibility';
// import Box from '@mui/material/Box';
// import Modal from '@mui/material/Modal';
// import PersonIcon from '@mui/icons-material/Person';
import EditIcon from '@mui/icons-material/Edit';
import Insert_fees from '../form/Insert_fees';
// import "./Staff.css"



const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '0px solid #000',
  boxShadow: 24,
  p: 4,
};


const Staff_Table = () => {
  const [search, setSearch] = useState("");
  const [c_data, setc_data] = useState([]);
  const [filterdata, setfilter] = useState([]);
  const [show,setshow] = useState(false);
  const [open, setOpen] = React.useState(false);
  const [studid,setsdtudid]=useState("");
  const [data, setData] = useState({
    id:"",
    name: "",
    fatherName:"",
    motherName: "",
    F_occupation: "",
    M_occupation: "",
    phone: "",
    address: "",
    city: "",
    gender: "male",
    Rollno:"",
    Class:""

});


  
  // -----------------------------datatable
  const getdata = async () => {
    try {
      const res = await axios.get("http://192.168.1.8:8070/fees/viewAll");
    
      setc_data(res.data.data);
      setfilter(res.data.data);
    
      
    }
    catch (error) {
      console.log(error);
    }
  };
const columns =[
  {
    name : "SID",
    selector : (row) => row.SID,
    sort:true
  },
  {
    name : "Fees",
    selector : (row) => row.Fees
  },
  {
    name : "AdminName",
    selector : (row) => row.AdminName
  },
  {
    name : "Date",
    selector : (row) => row.Date
  },
 {
  name:"action",
  cell:(row) => <>
  
  
      <EditIcon ></EditIcon>
      <VisibilityIcon></VisibilityIcon>
  </>

}
]
useEffect(() => {
  getdata();
}, []);

useEffect(() => {
  const result = c_data.filter(val => {
    return val.Name.toLowerCase().match(search.toLowerCase());

  });

  setfilter(result);
}, [search]);






// delete--------------------------
function deletestaff(_id){
  fetch(`http://192.168.1.8:8070/staff/delete/${_id}`,{
    method:"DELETE"
  }).then((r)=>{
    r.json().then((resp)=>{
      console.log(resp); 
      getdata();
    })
  })
}

// --------------------------------------------
  return (
    <>
     <h1 className=' text-center'>fees Data</h1>
        
        {
          show ?  <Insert_fees /> : ""
        }
         <button onClick={()=>setshow(!show)} className="insert"> { show ? "X":"fees"}</button>
       {
          show ? "": <DataTable
          title="Staff list"
          columns={columns}
          data={filterdata}
          pagination
          fixedHeader
          fixedHeaderScrollHeight='550px'
          selectableRows
          selectableRowsHighlight
          highlightOnHover
          subHeader
          subHeaderAlign="left"
          subHeaderComponent={
            <input
              type="text"
              placeholder='search'
              className='w-25 form-control'
              value={search}
              onChange={(event) => setSearch(event.target.value)} />
          }
        />
       }


    </>
  
  
  )
}

export default Staff_Table
