import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import DeleteIcon from '@mui/icons-material/Delete';
import Insert_Staff from '../form/Insert_Staff'
import VisibilityIcon from '@mui/icons-material/Visibility';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import PersonIcon from '@mui/icons-material/Person';
import EditIcon from '@mui/icons-material/Edit';
import "./Staff.css"
// import { Link,Outlet } from 'react-router-dom';


const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  border:"none",
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};


const Staff_Table = () => {
  const [search, setSearch] = useState("");
  const [c_data, setc_data] = useState([]);
  const [filterdata, setfilter] = useState([]);
  const [show,setshow] = useState(false);
  const [staffData,setStaffData] = useState([]);

  
  const getdata = async () => {
    try {
      const res = await axios.get("http://192.168.1.17:8070/staff/view?page=2&size=3");
      // console.log(res.data.data[0].Name)
      setc_data(res.data.data);
      setfilter(res.data.data);
    
      
    }
    catch (error) {
      console.log(error);
    }
  };
const columns =[
  {
    name : "Name",
    selector : (row) => row.Name,
    sort:true
  },
  {
    name : "Email",
    selector : (row) => row.Email
  },
  {
    name : "Degree",
    selector : (row) => row.Degree
  },
  {
    name : "Subject",
    selector : (row) => row.Subject
  },
  {
    name : "Salary",
    selector : (row) => row.Salary
  },
  {
    name : "Fresher",
    selector : (row) => row.Fresher
  },
 {
  name:"action",
  cell:(row) => <>
  
  
      {/* <Link to={`${row._id}`}><EditIcon className="edit"/></Link> */}
      <EditIcon onClick={()=>handleOpenUpdateupdate(row._id)}></EditIcon>
      <VisibilityIcon  onClick={() => handleOpenUpdate(row._id)} ></VisibilityIcon>
     <DeleteIcon onClick={()=>deletestaff(row._id)}></DeleteIcon>
  </>

}
]
useEffect(() => {
  getdata();
}, []);

useEffect(() => {
  const result = c_data.filter(val => {
    return val.Name.toLowerCase().match(search.toLowerCase());

  });

  setfilter(result);
}, [search]);
// view-------------------------------
const [open, setOpen] = useState(false);
  const handleOpenUpdate =async (id) =>{
     setOpen(true);
    try{
    const res = await axios.get(`http://192.168.1.17:8070/staff/viewById/${id}`);
    console.log(res.data.data)
    setStaffData(res.data.data)
    }catch(error)
    {
      console.log(error);
    }
  } 
  const handleClose = () => setOpen(false);

// ---------------update--------------------


const [openupdate, setOpenupdate] = useState(false);
const [staff_name,setstaff_name] = useState("");
const [email,setEmail] =useState("")
const [Degree,setDegree] = useState("");
const [subject,setSubject] =useState("");
const [salary,setSalary] =useState("");
const [Experience,setExperience] =useState("")


  const handleOpenUpdateupdate =async (id) =>{
     setOpenupdate(true);
    try{
    const res = await axios.get(`http://192.168.1.17:8070/staff/viewById/${id}`);
    console.log(res.data.data)
    setStaffData(res.data.data)
    }catch(error)
    {
      console.log(error);
    }
  } 
  const handleCloseupdate = () => setOpenupdate(false);


// const updatestaff =(id)=>{
   
//   let item={staff}
//   console.warn("item",item)
//   fetch(`http:/192.168.1.17:8070/staff/edit/${id}`, {
//     method: 'PUT',
//     headers:{
//       'Accept':'application/json',
//       'Content-Type':'application/json'
//     },
//     body:JSON.stringify(item)
//   }).then((result) => {
//     result.json().then((resp) => {
//       console.warn(resp)
//       handleOpenUpdateupdate()
//     })
//   })
// }


// delete--------------------------
function deletestaff(_id){
  fetch(`http://192.168.1.17:8070/staff/delete/${_id}`,{
    method:"DELETE"
  }).then((r)=>{
    r.json().then((resp)=>{
      console.log(resp); 
      getdata();
    })
  })
}

// --------------------------------------------
  return (
    <>
     <h1 className=' text-center'>Staff Data</h1>
        
        {
          show ?  <Insert_Staff /> : ""
        }
         <button onClick={()=>setshow(!show)} className="insert"> { show ? "X":"create Staff"}</button>
       {
          show ? "": <DataTable
          title="Staff list"
          columns={columns}
          data={filterdata}
          pagination
          fixedHeader
          fixedHeaderScrollHeight='550px'
          selectableRows
          selectableRowsHighlight
          highlightOnHover
          subHeader
          subHeaderAlign="left"
          subHeaderComponent={
            <input
              type="text"
              placeholder='search'
              className='w-25 form-control'
              value={search}
              onChange={(event) => setSearch(event.target.value)} />
          }
        />
       }
    

      {/* modal box */}
      <Modal
        keepMounted
        open={open}
        onClose={handleClose}
        aria-labelledby="keep-mounted-modal-title"
        aria-describedby="keep-mounted-modal-description">
        <Box sx={style}>
        <div className="d-flex">
             <PersonIcon style={{"width":"100%", "height":"100px"}} />
        </div>
