import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import Insert from  "../form/Insert"
import DeleteIcon from '@mui/icons-material/Delete';
// import VisibilityIcon from '@mui/icons-material/Visibility';
import EditIcon from '@mui/icons-material/Edit';
// import Box from '@mui/material/Box';
// import Modal from '@mui/material/Modal';
// import Button from '@mui/material/Button';
// import Typography from '@mui/material/Typography';

// import { Link, Outlet } from 'react-router-dom';
// const style = {
//   position: 'absolute',
//   top: '50%',
//   left: '50%',
//   transform: 'translate(-50%, -50%)',
//   width: 400,
//   bgcolor: 'background.paper',
//   border: '2px solid #000',
//   boxShadow: 24,
//   p: 4,
// };


  
const Userprofile = () => {
  const [search, setSearch] = useState("");
  const [c_data, setc_data] = useState([]);
  const [filterdata, setfilter] = useState([]);
  const [show,setshow] = useState(false)
//  const [stud,setstud] = useState(null)

// show---------------------------------------------
  // const [open, setOpen] = useState(false);

  // const handleOpen =async (id) =>{
  //   setOpen(true);
  //   try{
      
  //   const res = await axios.get(`http://192.168.1.41:8070/stud/view/${id}`);
  //   console.log(res.data.data)
  //   setstud(res.data.data)

   
  //   }catch(error)
  //   {
  //     console.log(error);
  //   }
  // } 
  // const handleClose = () => setOpen(false);
  //data table------------------------------------------- 
  const getdata = async () => {
    try {
      const res = await axios.get("http://192.168.1.41:8070/stud/view");
      console.log(res.data)
      setc_data(res.data);
      setfilter(res.data);
    }
    catch (error) {
      console.log(error);
    }
  };
  const columns = [
    {
      name: "FullName",
      selector: (row) => row.FullName,
      sort: true

    },
    {
      name: "fatherName ",
      selector: (row) => row.fatherName
    },
    {
      name: "motherName ",
      selector: (row) => row.motherName
    },
    // {
    //   name: "F_occupation",
    //   selector: (row) => row.F_occupation
    // },
    // {
    //   name: "M_occupation ",
    //   selector: (row) => row.M_occupation
    // },
    {
      name: "email",
      selector: (row) => row.email
    },
    {
      name: "phone ",
      selector: (row) => row.phone
    },
    {
      name: "address",
      selector: (row) => row.address
    },
    // {
    //   name: "city ",
    //   selector: (row) => row.city
    // },
    {
      name: "gender",
      selector: (row) => row.gender
    },
    {
      name: "Class",
      selector: (row) => row.Class
    },
    {
      name: "Rollno",
      selector: (row) => row.Rollno
    },
    {
       name: "action",
       cell: (row) => <>
        {/* <VisibilityIcon  onClick={() => handleOpen(row._id)} ></VisibilityIcon> */}
       <EditIcon></EditIcon>
        {/* <DeleteIcon onClick={()=>deletestudent(row._id)}>delete</DeleteIcon> */}
      
       </>
    }

  ]
  useEffect(() => {
    getdata();
  }, []);

  useEffect(() => {
    const result = c_data.filter(val => {
      console.log("filert")

      return val.email.toLowerCase().match(search.toLowerCase());

    });

    setfilter(result);
  }, [search]);
// delete----------------------------------------
  function deletestudent(_id){
    fetch(`http://192.168.1.41:8070/stud/view/delete/${_id}`,{
      method:"DELETE"
    }).then((r)=>{
      r.json().then((resp)=>{
        console.log(resp); 
        getdata();
      })
    })
  }

  // ------------------------------------------------------
  return (
    <div>
      <div className='page'>
      
        <h1 className=' text-center'>student Data</h1>
        
        {
          show ?  <Insert /> : ""
        }
         <button onClick={()=>setshow(!show)} className="insert"> { show ? "X":"create student"}</button>
       {
          show ? "": <DataTable
          title="Student list"
          columns={columns}
          data={filterdata}
          pagination
          fixedHeader
          fixedHeaderScrollHeight='550px'
          selectableRows
          selectableRowsHighlight
          highlightOnHover
          subHeader
          subHeaderAlign="left"
          subHeaderComponent={
            <input
              type="text"
              placeholder='search'
              className='w-25 form-control'
              value={search}
              onChange={(event) => setSearch(event.target.value)} />
          }
        />
       }
      </div>
      <div>
      


    </div>


    </div>
  )
}

export default Userprofile
