import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import "../form/Insert.css"
// import { Link, Outlet } from 'react-router-dom';



const Userprofile = () => {
  const [search, setSearch] = useState("");
  const [c_data, setc_data] = useState([]);
  const [filterdata, setfilter] = useState([]);
  const [show,setshow] = useState(false)
  
  const getdata = async () => {
    try {
      const res = await axios.get("http://192.168.1.45:8070/stud/view");
      setc_data(res.data);
      setfilter(res.data);
    }
    catch (error) {
      console.log(error);
    }
  };

  const columns = [
    {
      name: "FullName",
      selector: (row) => row.FullName,
      sort: true

    },
    {
      name: "fatherName ",
      selector: (row) => row.fatherName
    },
    {
      name: "motherName ",
      selector: (row) => row.motherName
    },
    {
      name: "F_occupation",
      selector: (row) => row.F_occupation
    },
    {
      name: "M_occupation ",
      selector: (row) => row.M_occupation
    },
    {
      name: "email",
      selector: (row) => row.email
    },
    {
      name: "phone ",
      selector: (row) => row.phone
    },
    {
      name: "address",
      selector: (row) => row.address
    },
    {
      name: "city ",
      selector: (row) => row.city
    },
    {
      name: "gender",
      selector: (row) => row.gender
    },
    {
      name: "Class",
      selector: (row) => row.Class
    },
    {
      name: "Rollno",
      selector: (row) => row.Rollno
    },
    // {
    //    name: "Action",
    //    cell: (row) => <button className='btn btn-primary' 
    //    onClick={()=> alert("hello")}>Edit</button>
    // }

  ]



  useEffect(() => {
    getdata();
  }, []);

  useEffect(() => {
    const result = c_data.filter(val => {
      console.log("filert")

      return val.email.toLowerCase().match(search.toLowerCase());

    });

    setfilter(result);
  }, [search]);




  const url="http://192.168.1.45:8070/stud/ragister"
  const [data,setdata] = useState({
    name: "",
    fatherName: "",
    motherName: "",
    F_occupation: "",
    M_occupation: "",
    email: "",
    phone: "",
    address: "",
    city: "",
    gender: "male",
    Rollno: " ",
    Class: "A",
 
      
  })
  // console.log(data)
 

  

  function insert(e){
    console.log("DAtA",data)
    e.preventDefault();
    axios.post(url,data)
    .then(res=>{
      console.log(res)
    }).catch((error)=>{
      console.log(error)
    })
  }



  function handle(e){
    const {name,value}=e.target
    setdata({...data, [name]:value})
  }
 
  return (
    <div>
      {/* <div className='sidebar'>
        <Sidebar />
      </div> */}
      <div className='page'>
        <h1 className=' text-center'>student Data</h1>
        {
          show ?  <div className="box">
                        
                        <form method="" action="" name='test_form' onSubmit={(e)=>insert(e)}>
                            <label>Student Name<span>*</span></label>
                            <input type="text" name='name' autoComplete='off'  onChange={(e)=>handle(e)} value={data.name} ></input>
                            <label>Father name<span>*</span></label>
                            <input type="text"  name='fatherName' autoComplete='off'  onChange={(e)=>handle(e)} value={data.fatherName}></input>
                            <label>Mother Name<span>*</span></label>
                            <input type="text"  name='motherName' autoComplete='off' onChange={(e)=>handle(e)}  value={data.motherName}></input>
                            <label>Father occupation<span>*</span></label>
                            <input type="text" name='F_occupation' autoComplete='off'  onChange={(e)=>handle(e)} value={data.F_occupation}></input>
                            <label>Mother occupation<span>*</span></label>
                            <input type="text"  name='M_occupation' autoComplete='off'  onChange={(e)=>handle(e)} value={data.M_occupation}></input>
                            <label>Email<span>*</span></label>
                            <input type="text" name='email' autoComplete='off' onChange={(e)=>handle(e)} value={data.email}></input>
                            <label>Mobile number<span>*</span></label>
                           
                            <input type="text"  name='phone' autoComplete='off' onChange={(e)=>handle(e)} value={data.phone} ></input>
                            {/* <label>Fees</label>
                            <div style={{backgroundColor:"white",width:"60px"}}><Switch  onClick={toggle} /></div> */}
                            <label>Address</label>
                            <textarea rows=" " cols=""  onChange={(e)=>handle(e)} name="address" value={data.address}> </textarea>
                            <label>City<span>*</span></label>
                            <input type="text"  name='city' autoComplete='off' onChange={(e)=>handle(e)}  value={data.city } ></input>
                            <label >Gender:</label>
                            <select name="gender"  onChange={(e)=>handle(e)} >
                                <option value="male">Male</option>
                                <option value="Female">Female</option>
                                <option value="Female">other</option>
                            </select>
                            <label>Rollno<span>*</span></label>
                            <input type="text" name='Rollno' autoComplete='off' onChange={(e)=>handle(e)} value={data.Rollno}></input>
                            <label>Class<span>*</span></label>
                            <input type="text" name='Class' autoComplete='off' onChange={(e)=>handle(e)} value={data.Class}></input>
                            
                            <button className="btn1" > submit</button>
                        </form>
                    </div>   : ""
        }
         <button onClick={()=>setshow(!show)} className="insert"> { show ? "X":"create student"}</button>
        {/* <Link to='insert' className='btn btn-info insert'>insert</Link>
         <Outlet /> */}
       {
          show ? "": <DataTable
          title="Student list"
          columns={columns}
          data={filterdata}
          pagination
          fixedHeader
          fixedHeaderScrollHeight='550px'
          selectableRows
          selectableRowsHighlight
          highlightOnHover
          subHeader
          subHeaderAlign="left"
          subHeaderComponent={
            <input
              type="text"
              placeholder='search'
              className='w-25 form-control'
              value={search}
              onChange={(event) => setSearch(event.target.value)} />
          }


        />
       }
        {/* <Outlet /> */}
      </div>


    </div>
  )
}

export default Userprofile
