import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import Insert from  "../form/Insert"
import DeleteIcon from '@mui/icons-material/Delete';
import VisibilityIcon from '@mui/icons-material/Visibility';
import EditIcon from '@mui/icons-material/Edit';


import Button from 'react-bootstrap/Button';
import PropTypes from 'prop-types';
// import Button from '@mui/material/Button';
import { styled } from '@mui/material/styles';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import Typography from '@mui/material/Typography';


const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  '& .MuiDialogContent-root': {
    padding: theme.spacing(2),
  },
  '& .MuiDialogActions-root': {
    padding: theme.spacing(1),
  },
}));

const BootstrapDialogTitle = (props) => {
  const { children, onClose, ...other } = props;

  return (
    <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
      {children}
      {onClose ? (
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{
            position: 'absolute',
            right: 8,
            top: 8,
            color: (theme) => theme.palette.grey[500],
          }}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </DialogTitle>
  );
};




BootstrapDialogTitle.propTypes = {
  children: PropTypes.node,
  onClose: PropTypes.func.isRequired,
};
  
const Userprofile = () => {

  const [stud,setStud] = useState()
  // const handleOpen = async (id) =>{
  //   const res = await axios.get(`http://192.168.1.30:8070/stud/view/${id}`);
  //   console.log(res)
  //   setStud(res.data.data)

  //   console.log("stud" , stud._id)
  // } 

  const [search, setSearch] = useState("");
  const [c_data, setc_data] = useState([]);
  const [filterdata, setfilter] = useState([]);


 const [anchorEl, setAnchorEl] = useState(null);
 const [view,setView] = useState()
const [show, setShow] = useState(false);
 const [showModel, setShowModel] = useState(false);
 

//  const handleClose = () => setShowModel(false);
//  const handleShow = () => setShowModel(true);

const [open, setOpen] = useState(false);

const handleClickOpen = async(id) => {
  setOpen(true);
  const data = await axios.get(`http://192.168.1.30:8070/stud/view/${id}`).then((res)=>{
    console.log(res)
  }).catch((err)=>{
    log
  })
};
const handleClose = () => {
  setOpen(false);
};

// show---------------------------------------------
  // const [open, setOpen] = useState(false);

 
  




  //data table------------------------------------------- 
  const getdata = async () => {
    try {
      const res = await axios.get("http://192.168.1.30:8070/stud/view");
      console.log(res.data)
      setc_data(res.data);
      setfilter(res.data);
    }
    catch (error) {
      console.log(error);
    }
  };
  const columns = [
    {
      name: "FullName",
      selector: (row) => row.FullName,
      sort: true

    },
    {
      name: "fatherName ",
      selector: (row) => row.fatherName
    },
    {
      name: "motherName ",
      selector: (row) => row.motherName
    },
    // {
    //   name: "F_occupation",
    //   selector: (row) => row.F_occupation
    // },
    // {
    //   name: "M_occupation ",
    //   selector: (row) => row.M_occupation
    // },
    {
      name: "email",
      selector: (row) => row.email
    },
    {
      name: "phone ",
      selector: (row) => row.phone
    },
    {
      name: "address",
      selector: (row) => row.address
    },
    // {
    //   name: "city ",
    //   selector: (row) => row.city
    // },
    {
      name: "gender",
      selector: (row) => row.gender
    },
    {
      name: "Class",
      selector: (row) => row.Class
    },
    {
      name: "Rollno",
      selector: (row) => row.Rollno
    },
    {
       name: "action",
       cell: (row) => <>
        <VisibilityIcon  onClick={()=>handleClickOpen(row._id)}/>
       <EditIcon></EditIcon>
        <DeleteIcon onClick={()=>deletestudent(row._id)}>delete</DeleteIcon>
      
       </>
    }

  ]
  useEffect(() => {
    getdata();
  }, []);

  useEffect(() => {
    const result = c_data.filter(val => {
      console.log("filert")

      return val.email.toLowerCase().match(search.toLowerCase());

    });

    setfilter(result);
  }, [search]);
// delete----------------------------------------
  function deletestudent(_id){
    fetch(`http://192.168.1.30:8070/stud/view/delete/${_id}`,{
      method:"DELETE"
    }).then((r)=>{
      r.json().then((resp)=>{
        console.log(resp); 
        getdata();
      })
    })
  }


  // ------------------------------------------------------
  return (
    <>
      <div className='page'>
      
        <h1 className=' text-center'>student Data</h1>
        
        {
          show ?  <Insert /> : ""
        }
         <button onClick={()=>setShow(!show)} className="insert"> { show ? "X":"create student"}</button>
       {
          show ? "": <DataTable
          title="Student list"
          columns={columns}
          data={filterdata}
          pagination
          fixedHeader
          fixedHeaderScrollHeight='550px'
          selectableRows
          selectableRowsHighlight
          highlightOnHover
          subHeader
          subHeaderAlign="left"
          subHeaderComponent={
            <input
              type="text"
              placeholder='search'
              className='w-25 form-control'
              value={search}
              onChange={(event) => setSearch(event.target.value)} />
          }
        />
       }
      </div>



 

      {/* <Modal show={showModel} onHide={handleClose} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {stud._id}
          
        </Modal.Body>
        <Modal.Footer>
          <button variant="secondary" onClick={handleClose}>
            Close
          </button>
          <button variant="primary" onClick={handleClose}>
            Save Changes
          </button>
        </Modal.Footer>
      </Modal> */}

      <BootstrapDialog
        onClose={handleClose}
        aria-labelledby="customized-dialog-title"
        open={open}
      >
        <BootstrapDialogTitle id="customized-dialog-title" onClose={handleClose}>
          Modal title
        </BootstrapDialogTitle>
        <DialogContent dividers>
          <Typography gutterBottom>
            Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
            dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
            consectetur ac, vestibulum at eros.
          </Typography>
          <Typography gutterBottom>
            Praesent commodo cursus magna, vel scelerisque nisl consectetur et.
            Vivamus sagittis lacus vel augue laoreet rutrum faucibus dolor auctor.
          </Typography>
          <Typography gutterBottom>
            Aenean lacinia bibendum nulla sed consectetur. Praesent commodo cursus
            magna, vel scelerisque nisl consectetur et. Donec sed odio dui. Donec
            ullamcorper nulla non metus auctor fringilla.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose}>
            Save changes
          </Button>
        </DialogActions>
      </BootstrapDialog>

  


    </>
  )
}

export default Userprofile
