import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import DeleteIcon from '@mui/icons-material/Delete';
import Insert_Staff from '../form/Insert_Staff'
import VisibilityIcon from '@mui/icons-material/Visibility';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import PersonIcon from '@mui/icons-material/Person';
import EditIcon from '@mui/icons-material/Edit';
import "./Staff.css"

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  border:"none",
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};


const Staff_Table = () => {
  const [search, setSearch] = useState("");
  const [c_data, setc_data] = useState([]);
  const [filterdata, setfilter] = useState([]);
  const [show,setshow] = useState(false);
  const [staff_Name,setstaff_Name] = useState("");
  const [Degree,setDegree] = useState("");
  const [salary,setSalary] = useState("");
  const [Subject,setSubject]= useState("");
  const [Email,setEmail] = useState("");
  const [Fresher,setFresher] = useState("")
  const [Experience,setExperience] = useState("")

  const [staff_Name1,setstaff_Name1] = useState()
  const getdata = async () => {
    try {
      const res = await axios.get("http://192.168.1.30:8070/staff/view?page=2&size=3");
      console.log(res.data.data[0].Name)
      setc_data(res.data.data);
      setfilter(res.data.data);
      // console.log(res.data.data[0].Name)
      setstaff_Name1(res.data.data[0].Name)
      
    }
    catch (error) {
      console.log(error);
    }
  };
const columns =[
  {
    name : "Name",
    selector : (row) => row.Name,
    sort:true
  },
  {
    name : "Email",
    selector : (row) => row.Email
  },
  {
    name : "Degree",
    selector : (row) => row.Degree
  },
  {
    name : "Subject",
    selector : (row) => row.Subject
  },
  {
    name : "Salary",
    selector : (row) => row.Salary
  },
  {
    name : "Fresher",
    selector : (row) => row.Fresher
  },
 {
  name:"action",
  cell:(row) => <>
  {
      show? <div className="box">
              
              <form method="" action="" name='test_form' >
                                    <label>Name<span>*</span></label>
                                    <input type="text" name='Name' autoComplete='off' value={staff_Name1} ></input>
                                    <label>Email<span>*</span></label>
                                    <input type="text" name='Email' autoComplete='off' ></input>
                                    <label>Degree<span>*</span></label>
                                    <input type="text" name='Degree' autoComplete='off'></input>
                                    <label>Subject<span>*</span></label>
                                    <input type="text" name='Subject' autoComplete='off'></input>
                                    <label>Salary<span>*</span></label>
                                    <input type="text" name='Salary' autoComplete='off'></input>
                                    <label>Are you Fresher ?</label>
                                    <select name="Fresher"  >
                                        <option value="0">0</option>
                                        <option value="1">1</option>
                                    </select>
                                    <label>Experience<span>*</span></label>
                                    <input type="text" name='Experience' autoComplete='off'></input>
                                    <button className="btn1" > submit</button>
                                </form>     
                  </div>: " "  }
      <EditIcon  onClick={()=>setshow(!show)}></EditIcon>
      <VisibilityIcon  onClick={() => handleOpen(row._id)} ></VisibilityIcon>
     <DeleteIcon onClick={()=>deletestaff(row._id)}></DeleteIcon>
  </>

}
]
useEffect(() => {
  getdata();
}, []);

useEffect(() => {
  const result = c_data.filter(val => {
    return val.Name.toLowerCase().match(search.toLowerCase());

  });

  setfilter(result);
}, [search]);
// view-------------------------------
const [open, setOpen] = useState(false);

  const handleOpen =async (id) =>{
     setOpen(true);
    try{
      
    const res = await axios.get(`http://192.168.1.30:8070/staff/viewById/${id}`);
    console.log(res.data.data)
    setstaff_Name(res.data.data.Name);
    setDegree(res.data.data.Degree);
    setSalary(res.data.data.Salary);
    setSubject(res.data.data.Subject);
    setEmail(res.data.data.Email);
    setFresher(res.data.data.Fresher);
    setExperience(res.data.data.Experience);
   
    }catch(error)
    {
      console.log(error);
    }
  } 
  const handleClose = () => setOpen(false);
// -----------------------------------
// update-----------------------------






// delete--------------------------
function deletestaff(_id){
  fetch(`http://192.168.1.30:8070/staff/delete/${_id}`,{
    method:"DELETE"
  }).then((r)=>{
    r.json().then((resp)=>{
      console.log(resp); 
      getdata();
    })
  })
}

// --------------------------------------------
  return (
    <>
     <h1 className=' text-center'>Staff Data</h1>
        
        {
          show ?  <Insert_Staff /> : ""
        }
         <button onClick={()=>setshow(!show)} className="insert"> { show ? "X":"create Staff"}</button>
       {
          show ? "": <DataTable
          title="Staff list"
          columns={columns}
          data={filterdata}
          pagination
          fixedHeader
          fixedHeaderScrollHeight='550px'
          selectableRows
          selectableRowsHighlight
          highlightOnHover
          subHeader
          subHeaderAlign="left"
          subHeaderComponent={
            <input
              type="text"
              placeholder='search'
              className='w-25 form-control'
              value={search}
              onChange={(event) => setSearch(event.target.value)} />
          }
        />
       }
    

      {/* modal box */}
      <Modal
        keepMounted
        open={open}
        onClose={handleClose}
        aria-labelledby="keep-mounted-modal-title"
        aria-describedby="keep-mounted-modal-description">
        <Box sx={style}>
        <div className="d-flex">
             <PersonIcon style={{"width":"100%", "height":"100px"}} />
        </div>
          <div className='d-flex view_staff'>
             <h6 >Name:</h6><p>{staff_Name}</p>
          </div>
          <div className='d-flex view_staff'>
            <h6>Email:</h6><p>{Email}</p>
          </div>
          <div className='d-flex view_staff'>
          <h6>Degree:</h6><p>{Degree}</p>
          </div>
          <div className='d-flex view_staff'>
            <h6>salary:</h6><p>{salary}</p>
          </div>
          <div className='d-flex view_staff'>
            <h6>Subject:</h6><p>{Subject}</p>
          </div>
          <div className='d-flex view_staff'>
          <h6>Fresher:</h6><p>{Fresher}</p>
          </div>
          <div className='d-flex view_staff'>
