import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'


const Userprofile= () => {
  const [search,setSearch] = useState("");
  const [c_data,setdata] = useState([]);
  const [filterdata,setfilter] = useState([]);
  const getdata = async ()=>{
      try{
        const res= await axios.get("http://192.168.1.69:8070/stud/view");
        setdata(res.data);
        setfilter(res.data);
      }
      catch(error)
      {
        console.log(error);
      }
  };

const columns =[
  {
    name : "FullName",
    selector : (row) => row.FullName,
    sort:true
    
  },
  {
    name : "fatherName ",
    selector : (row) => row.fatherName
  },
  {
    name : "motherName ",
    selector : (row) => row.motherName
  },
  {
    name : "F_occupation",
    selector : (row) => row.F_occupation
  },
  // {
  //    name: "Action",
  //    cell: (row) => <button className='btn btn-primary' 
  //    onClick={()=> alert("hello")}>Edit</button>
  // }

]



useEffect( ()=>{
  getdata();
},[]);

useEffect( () =>{
   const result = c_data.filter(val => {
  
    return val.name.toLowerCase().match(search.toLowerCase());

   });
   
   setfilter(result);
}, [search]);
  return (
    <div>
    <h1 className=' text-center'>User Data</h1>
    <DataTable
    title="User list"  
    columns={columns}
     data={filterdata} 
     pagination
      fixedHeader
      fixedHeaderScrollHeight='550px'
      selectableRows
      selectableRowsHighlight
      highlightOnHover
      // actions={
      //   <button className='btn btn-sm btn-info'>export</button>
      // }
      subHeader
      subHeaderAlign="right"
      subHeaderComponent={
        <input
         type="text"
         placeholder='search'
         className='w-25 form-control' 
         value={search}
         onChange={(event) => setSearch(event.target.value)} />
      }
     
     
     />
    
    
    </div>
  )
}

export default Userprofile
