import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'


const Table = () => {
  // const [search,setSearch] = useState("");
  const [c_data,setData] = useState([]);
  const [filterData,setFilter] = useState([]);
  const getdata = async ()=>{
      try{
        const res= await axios.get("http://192.168.1.41:8070/staff/view?page=2&size=2");
        setData(res.data);
        setFilter(res.data);
      }
      catch(error)
      {
        console.log(error);
      }
  };

const columns =[
  {
    name : "Name",
    selector : (row) => row.Name,
    sort:true
    
  },
  {
    name : "Email",
    selector : (row) => row.Email
  },
  {
    name : "Degree",
    selector : (row) => row.Degree
  },
  {
    name : "Subject",
    selector : (row) => row.Subject
  },
  {
    name : "Salary",
    selector : (row) => row.Salary
  },
  {
    name : "Fresher",
    selector : (row) => row.Fresher
  },
  {
     name: "Action",
     cell: (row) => <button className='btn btn-primary'>show</button>
  }

]



useEffect( ()=>{
  getdata();
},[]);

// useEffect( () =>{
//    const result = c_data.filter(val => {
  
//     return val.Name.toLowerCase().match(search.toLowerCase());

//    });
   
//    setFilter(result);
// }, [search]);
  return (
    <div>
    <h1 className=' text-center'>Data Table</h1>
    <DataTable
    title="list"  
    columns={columns}
     data={c_data} 
     pagination
      fixedHeader
      fixedHeaderScrollHeight='550px'
      selectableRows
      selectableRowsHighlight
      highlightOnHover
      // subHeader
      // subHeaderAlign="right"
      // subHeaderComponent={
      //   <input
      //    type="text"
      //    placeholder='search'
      //    className='w-25 form-control' 
      //    value={search}
      //    onChange={(event) => setSearch(event.target.value)} />
      // }
       />
    
    
    </div>
  
  
  )
}

export default Table
