      {/* modal box */}
      <Modal
        keepMounted
        open={open}
        onClose={handleClose}
        aria-labelledby="keep-mounted-modal-title"
        aria-describedby="keep-mounted-modal-description">
        <Box sx={style}>
        <div className="d-flex">
             <PersonIcon style={{"width":"100%", "height":"100px"}} />
        </div>
          <div className='d-flex view_staff'>
             <h6 >Name:</h6><p>{staffData.Name}</p>
          </div>
          <div className='d-flex view_staff'>
            <h6>Email:</h6><p>{staffData.Email}</p>
          </div>
          <div className='d-flex view_staff'>
          <h6>Degree:</h6><p>{staffData.Degree}</p>
          </div>
          <div className='d-flex view_staff'>
            <h6>Salary:</h6><p>{staffData.Salary}</p>
          </div>
          <div className='d-flex view_staff'>
            <h6>Subject:</h6><p>{staffData.Subject}</p>
          </div>
          <div className='d-flex view_staff'>
          <h6>Fresher:</h6><p>{staffData.Fresher}</p>
          </div>
          <div className='d-flex view_staff'>
          <h6>Experience:</h6><p>{staffData.Experience}</p>
          </div>
        </Box>
      </Modal>




      <Modal
        keepMounted
        open={openUpdate}
        onClose={handleCloseUpdate}
        aria-labelledby="keep-mounted-modal-title"
        aria-describedby="keep-mounted-modal-description">
        <Box sx={style}>
       
              
              <form method="" action="" className='updateform' onSu>
                                    <label>Name<span>*</span></label>
                                    <input type="text" defaultValue={edit.Name} onChange={(e)=>handleedit(e)}  ></input>
                                    <label>Email<span>*</span></label>
                                    <input type="text"  defaultValue={edit.Email} onChange={(e)=>handleedit(e)} ></input>
                                    <label>Degree<span>*</span></label>
                                    <input type="text" defaultValue={edit.Degree} onChange={(e)=>handleedit(e)} ></input>
                                    <label>Subject<span>*</span></label>
                                    <input type="text" defaultValue={edit.Salary} onChange={(e)=>handleedit(e)} ></input>
                                    <label>Salary<span>*</span></label>
                                    <input type="text" defaultValue={edit.Subject} onChange={(e)=>handleedit(e)} ></input>
                                    <label>Are you Fresher ?</label>
                                    <select name="Fresher"   >
                                        <option defaultValue={edit.Fresher}>0</option>
                                        <option defaultValue={edit.Fresher}>1</option>
                                    </select>
                                    <label>Experience<span>*</span></label>
                                    <input type="text"  defaultValue={edit.Experience} onChange={(e)=>handleedit(e)} ></input>
                                    <button className="btn1" > update user</button>
                                </form>     
                  
                  
        </Box>
      </Modal>

    </>
  
  
  )
}

