import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import Insert from "../table/S"


const Student_Table = () => {
  const [search, setSearch] = useState("");
  const [c_data, setc_data] = useState([]);
  const [filterdata, setfilter] = useState([]);
  const [show,setshow] = useState(false)
  const getdata = async () => {
    try {
      const res = await axios.get("http://192.168.1.41:8070/staff/view?page=2&size=2");
      console.log(res.data.data)
      setc_data(res.data.data);
      setfilter(res.data.data);
    }
    catch (error) {
      console.log(error);
    }
  };

const columns =[
  {
    name : "Name",
    selector : (row) => row.Name,
    sort:true
    
  },
  {
    name : "Email",
    selector : (row) => row.Email
  },
  {
    name : "Degree",
    selector : (row) => row.Degree
  },
  {
    name : "Subject",
    selector : (row) => row.Subject
  },
  {
    name : "Salary",
    selector : (row) => row.Salary
  },
  {
    name : "Fresher",
    selector : (row) => row.Fresher
  },
  // {
  //    name: "Action",
  //    cell: (row) => <button className='btn btn-primary'>show</button>
  // }

]



useEffect(() => {
  getdata();
}, []);

useEffect(() => {
  const result = c_data.filter(val => {
    return val.Name.toLowerCase().match(search.toLowerCase());

  });

  setfilter(result);
}, [search]);
  return (
    <div>
    <h1 className=' text-center my-3'>Staff List</h1>
        {
          show ?  <Insert /> : ""
        }

    <DataTable  
    columns={columns}
     data={filterdata} 
     pagination
      fixedHeader
      fixedHeaderScrollHeight='550px'
      selectableRows
      selectableRowsHighlight
      highlightOnHover
      subHeader
      subHeaderAlign="left"
      subHeaderComponent={
        <input
         type="text"
         placeholder='search'
         className='w-25 form-control my-3' 
         value={search}
         onChange={(event) => setSearch(event.target.value)} />
      }
       />
    
    
    </div>
  
  
  )
}

export default Student_Table
