import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import "../form/Insert"
// import { Link, Outlet } from 'react-router-dom';



const Userprofile = () => {
  const [search, setSearch] = useState("");
  const [c_data, setc_data] = useState([]);
  const [filterdata, setfilter] = useState([]);
  const [show,setshow] = useState(false)
  const [msg,setmsg] = useState("");
  const getdata = async () => {
    try {
      const res = await axios.get("http://192.168.1.45:8070/stud/view");
      setc_data(res.data);
      setfilter(res.data);
    }
    catch (error) {
      console.log(error);
    }
  };
    
  function deletestudent(_id){
    fetch(`http://192.168.1.45:8070/stud/view/delete/${_id}`,{
      method:"DELETE"
    }).then((r)=>{
      r.json().then((resp)=>{
        console.log(resp); 
        getdata();
      })
    })
  }

  const columns = [
    {
      name: "FullName",
      selector: (row) => row.FullName,
      sort: true

    },
    // {
    //   name: "fatherName ",
    //   selector: (row) => row.fatherName
    // },
    // {
    //   name: "motherName ",
    //   selector: (row) => row.motherName
    // },
    {
      name: "F_occupation",
      selector: (row) => row.F_occupation
    },
    {
      name: "M_occupation ",
      selector: (row) => row.M_occupation
    },
    {
      name: "email",
      selector: (row) => row.email
    },
    {
      name: "phone ",
      selector: (row) => row.phone
    },
    {
      name: "address",
      selector: (row) => row.address
    },
    {
      name: "city ",
      selector: (row) => row.city
    },
    {
      name: "gender",
      selector: (row) => row.gender
    },
    {
      name: "Class",
      selector: (row) => row.Class
    },
    {
      name: "Rollno",
      selector: (row) => row.Rollno
    },
    {
       name: "delete",
       cell: (row) => <button className='btn btn-primary' onClick={()=>deletestudent(row._id)}>delete</button>
    }

  ]



  useEffect(() => {
    getdata();
  }, []);

  useEffect(() => {
    const result = c_data.filter(val => {
      console.log("filert")

      return val.email.toLowerCase().match(search.toLowerCase());

    });

    setfilter(result);
  }, [search]);




  const url="http://192.168.1.45:8070/stud/ragister"
  const [data,setdata] = useState({
    name: "",
    fatherName: "",
    motherName: "",
    F_occupation: "",
    M_occupation: "",
    email: "",
    phone: "",
    address: "",
    city: "",
    gender: "male",
    Rollno: " ",
    Class: "A",
 
      
  })
  // console.log(data)
 

  

  function insert(e){
    console.log("DAtA",data)
    e.preventDefault();
    axios.post(url,data)
    .then(res=>{
      console.log(res)
      setmsg(res.data.message)
    }).catch((error)=>{
      console.log(error)
    })
  }



  function handle(e){
    const {name,value}=e.target
    setdata({...data, [name]:value})
  }
 
  return (
    <div>
      {/* <div className='sidebar'>
        <Sidebar />
      </div> */}
      <div className='page'>
        <h1 className=' text-center'>student Data</h1>
        {
          show ?  <Insert /> : ""
        }
         <button onClick={()=>setshow(!show)} className="insert"> { show ? "X":"create student"}</button>
        {/* <Link to='insert' className='btn btn-info insert'>insert</Link>
         <Outlet /> */}
       {
          show ? "": <DataTable
          title="Student list"
          columns={columns}
          data={filterdata}
          pagination
          fixedHeader
          fixedHeaderScrollHeight='550px'
          selectableRows
          selectableRowsHighlight
          highlightOnHover
          subHeader
          subHeaderAlign="left"
          subHeaderComponent={
            <input
              type="text"
              placeholder='search'
              className='w-25 form-control'
              value={search}
              onChange={(event) => setSearch(event.target.value)} />
          }


        />
       }
        {/* <Outlet /> */}
      </div>


    </div>
  )
}

export default Userprofile
