import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import { NavLink } from 'react-router-dom';



const Userprofile = () => {
  const [search, setSearch] = useState("");
  const [c_data, setdata] = useState([]);
  const [filterdata, setfilter] = useState([]);
  const getdata = async () => {
    try {
      const res = await axios.get("http://192.168.1.20:8070/stud/view");
      setdata(res.data);
      setfilter(res.data);
    }
    catch (error) {
      console.log(error);
    }
  };

  const columns = [
    {
      name: "FullName",
      selector: (row) => row.FullName,
      sort: true

    },
    {
      name: "fatherName ",
      selector: (row) => row.fatherName
    },
    {
      name: "motherName ",
      selector: (row) => row.motherName
    },
    {
      name: "F_occupation",
      selector: (row) => row.F_occupation
    },
    {
      name: "M_occupation ",
      selector: (row) => row.M_occupation
    },
    {
      name: "email",
      selector: (row) => row.email
    },
    {
      name: "phone ",
      selector: (row) => row.phone
    },
    {
      name: "address",
      selector: (row) => row.address
    },
    {
      name: "city ",
      selector: (row) => row.city
    },
    {
      name: "gender",
      selector: (row) => row.gender
    },
    // {
    //    name: "Action",
    //    cell: (row) => <button className='btn btn-primary' 
    //    onClick={()=> alert("hello")}>Edit</button>
    // }

  ]



  useEffect(() => {
    getdata();
  }, []);

  useEffect(() => {
    const result = c_data.filter(val => {

      return val.FullName.toLowerCase().match(search.toLowerCase());

    });

    setfilter(result);
  }, [search]);
  return (
    <div>
      {/* <div className='sidebar'>
        <Sidebar />
      </div> */}
        <div className='page'>
        <h1 className=' text-center'>student Data</h1>
      <DataTable
        title="Student list"
        columns={columns}
        data={filterdata}
        pagination
        fixedHeader
        fixedHeaderScrollHeight='550px'
        selectableRows
        selectableRowsHighlight
        highlightOnHover
        actions={
         <NavLink to> <button className='btn btn-lg btn-info' >insert</button></NavLink>
        }
        subHeader
        subHeaderAlign="left"
        subHeaderComponent={
          <input
            type="text"
            placeholder='search'
            className='w-25 form-control'
            value={search}
            onChange={(event) => setSearch(event.target.value)} />
        }


      />
        </div>


    </div>
  )
}

export default Userprofile
