  function logout(e){
    e.preventDefault();
    localStorage.clear();
   
    axios.post("http://192.168.1.39:8070/admin/login")
    .then(res=>{
      if(res.data.token){
          window.location = '/dashborad'
      }else{
          window.location = '/'      
      }
    })
  }

  //  -----------------------------VIEW
const [data,setdata] = use
const handleClickOpen = async (id) => {
    setOpen(true);
    const data = await axios.get(`http://192.168.1.39:8070/attendance/viewById/${id}`).then((res) => {
      console.log(res.data.data)
     
      setData(res.data.data)
    }).catch((err) => {
      console.log(err);
    })
  };
  const handleClose = () => {
    setOpen(false);
  
  };
  
  return (
    <div>
       <div className='container mx-0 px-0'>
        <div className='log_out_div'>
            <button className='log_out' onClick={(e)=> logout(e)}>Log out</button>
        </div>
            <div className=' d-flex justify-content-center'>
            <Cards/>
            <Cards/>
            <Cards/>
            </div>
            
            
         </div> 
    </div>
  )
}

export default Dashborad
