import React, { useState } from 'react'
import "./Login"


const Login = () => {
      const [email,setemail] = useState("")
      const [pass,setpass] = useState("")
      const [emailerr,setemailerr] = useState(false)
      const [passerr,setpasserr] = useState(false)
    function validation(e)
     {  
         e.preventDefault()
    }
     function user(e)
    { 
        if(e.target.value.match(/^[a-zA-Z0-9_\-]+@[a-zA-Z]+\.[a-zA-Z]{2,3}$/))
        {
            setemailerr(false)
        }
        else{
            setemailerr(true)
        }
    //    console.log(e.target.value)
    }
    function password(e)
    { 
        if(e.target.value.length > 8)
        {
            setpasserr(false)
        }
        else{
            setpasserr(true)
        }
    //    console.log(e.target.value)
    }
  return (
    <>
   <div className='main_div'>
   <div className="main">
        <div className="box">
            <h3>Login To Your Account</h3>
            <form method="" action="" onSubmit={validation}>
                    <input type="text" placeholder='Email' autocomplete="off" onChange={user}/>
                   {emailerr ? <span>user not valid</span>:""}
                    <input type="password" placeholder='password' autocomplete="off" onChange={password}/>
                    {passerr ? <span>invalid password</span>:""}
                    <div className='checkbox'>
                    <input  type="checkbox" className='chk'style={{height:"15px" ,width:"30px"}}></input><p>Agree with Terms & condition</p>
                    </div>

                <button type='submit'  className="btn"> Login</button>
            </form>
        </div>
    </div>

   </div>
   


    </>
  )
}

export default Login
